script:
frappe.ui.form.on('Lead', {
    refresh(frm) {
        // Example rule: based on custom delivery or intent
        const urgency = frm.doc.expected_delivery;  // create this field if not available
        const intent = frm.doc.intent;              // another custom field

        let temp = "Cold";

        if (urgency && urgency <= 7) {
            temp = "Hot";
        } else if (urgency && urgency <= 15) {
            temp = "Warm";
        }

        frm.set_value("lead_temperature", temp);
    }
});

frappe.listview_settings['Lead'] = {
    filters: [["lead_temperature", "!=", ""]],  // Show leads where temperature is set

    onload: function (listview) {
        listview.page.add_dropdown(__('Filter by Temperature'), [
            {
                label: 'ðŸ”¥ Hot',
                action: function () {
                    listview.filter_area.clear();
                    listview.filter_area.add([['Lead', 'lead_temperature', '=', 'Hot']]);
                }
            },
            {
                label: 'ðŸŒ¤ï¸ Warm',
                action: function () {
                    listview.filter_area.clear();
                    listview.filter_area.add([['Lead', 'lead_temperature', '=', 'Warm']]);
                }
            },
            {
                label: 'â„ï¸ Cold',
                action: function () {
                    listview.filter_area.clear();
                    listview.filter_area.add([['Lead', 'lead_temperature', '=', 'Cold']]);
                }
            },
            {
                label: 'ðŸ§¹ Clear Filter',
                action: function () {
                    listview.filter_area.clear();
                }
            }
        ]);
    }
};

