frappe.listview_settings['Lead'] = {
    get_indicator: function (doc) {
        if (doc.lead_temperature === "Hot") {
            return [__("Hot"), "red", "lead_temperature,=,Hot"];
        } else if (doc.lead_temperature === "Warm") {
            return [__("Warm"), "orange", "lead_temperature,=,Warm"];
        } else if (doc.lead_temperature === "Cold") {
            return [__("Cold"), "blue", "lead_temperature,=,Cold"];
        }
    }
};
